

/*


https://leetcode.com/problems/repeated-string-match/

Given two strings a and b, return the minimum number of times you should repeat
string a so that string b is a substring of it. If it is impossible for b
to be a substring of a after repeating it, return -1
 */

public class StringRepeatMatch {


    public static int find(String a, String b) {

        int repeat = Math.max(1,(int)Math.ceil((float)b.length()/a.length()));
//        StringBuilder newA = new StringBuilder(a.repeat(repeat));
        StringBuilder newA = new StringBuilder(a);
        for ( int i = 1; i < repeat; ++i ) newA.append(a);
        /* just append "a" 2 more times */
        int start =  repeat-1;
        for( int i = start; i < start + 2 ; ++i){
            if ( newA.indexOf(b) != -1 ) return i+1;
            newA.append(a);
        }
        return -1;
    }

    public static void main(String[] args){
//        String str1 = "a";
//        String str2 = "";

        String str1 = "abcd";
        String str2 = "cdabcdab";
        long t = System.currentTimeMillis();
        System.out.println( find(str1, str2));
        System.out.println("time = " + ( System.currentTimeMillis() - t ) );
    }
}
